(()=>{var e=class{constructor(t){if(this.element=document.querySelector(t),!this.element)throw new Error(`Element with selector ${t} not found`);this.updateTime(),this.start()}formatTime(t){let n=l=>String(l).padStart(2,"0"),i=t.toLocaleString("en-US",{timeZone:"Europe/Amsterdam",hour12:!1,hour:"2-digit",minute:"2-digit",second:"2-digit"}),[s,a,m]=i.split(":");return`${s}:${a}:${m}`}updateTime(){let t=new Date;this.element.textContent=this.formatTime(t)}start(){this.interval=setInterval(()=>this.updateTime(),1e3)}stop(){clearInterval(this.interval)}};function o(){let r="start-hidden";document.querySelectorAll(`[${r}]`).forEach(n=>{n.removeAttribute(r)})}var p=new e("h1");o();})();
